exports.getHomonizedData = function (data, type) {
    var dataCount = data.length;
    var homonizedData = new Array();
    for (var i = 0; i < dataCount; i++) {
        var homonizedValue = 0;
        if (i === 0) {
            homonizedValue = (data[i] + data[(i + 1)] + data[(i + 2)]) / 3;
        } else if (i === 1) {
            homonizedValue = (data[(i - 1)] + data[i] + data[(i + 1)] + data[(i + 2)]) / 4;
        } else if (i === (dataCount - 2)) {
            homonizedValue = (data[(i - 2)] + data[(i - 1)] + data[i] + data[(i + 1)]) / 4;
        } else if (i === (dataCount - 1)) {
            homonizedValue = (data[(i - 2)] + data[(i - 1)] + data[i]) / 3;
        } else {
            homonizedValue = (data[(i - 2)] + data[(i - 1)] + data[i] + data[(i + 1)] + data[(i + 2)]) / 5;
        }
        if (type === 'int') homonizedValue = Math.round(homonizedValue);
        homonizedData.push(homonizedValue);
    }
    return homonizedData;
};