exports.getStrippedValues = function (data, type) {
    var strippedValues = new Array(),
        value          = 0;
        
    for (var index in data) {
        value = (type === 'int') ?
            parseInt(data[index].value) : parseFloat(data[index].value);
        strippedValues.push(value);
    }
    return strippedValues;
};