var values = new Array();

exports.reset = function () {
    values = new Array();
};

exports.setValues = function (valueArray) {
    values = valueArray;
};

exports.addValue = function (value) {
    values.push(value);
};

exports.getMedian = function () {
    if (values.length < 1) {
        return 0;
    }
    var medianIndex = (values.length > 1) ? ((values.length - 1) / 2) : 0;
    values.sort();
    if (medianIndex % 1 === 0) {
        return values[medianIndex];
    } else {
        return (values[Math.floor(medianIndex)] + values[Math.ceil(medianIndex)]) / 2;
    }
};