var values        =  new Array(),
    averageModule = require('average');

exports.reset = function () {
    values = new Array();
};

exports.setValues = function (valueArray) {
    values = valueArray;
};

exports.getStandardDeviation = function () {
    var average = 0
        totalDeviation = 0;
        
    averageModule.setValues(values);
    average = averageModule.getAverage();
        
    for (var i = 0; i < values.length; i++) {
        totalDeviation += Math.pow(values[i] - average, 2);
    }
    return Math.sqrt(totalDeviation / values.length);
};